# itens, keys e values
aluno = {'Nome': ' Bruno',
         'idade': 20,
         'nota': '10',
         'aprovação': True,
         'materias': ['física', 'matemática', 'Modelagem de dados']} # lista dentro de dicionário
print(aluno)
print(aluno.get('materias'))
print(aluno.keys())
print(aluno.values())
print(aluno.items())
print(len(aluno))